mapper.select:
select a.activity_id activityId,a.activity_name activityName,a.activity_type activityType,
a.channel_id channelId,b.province_name provinceName,a.start_time startTime,a.end_time endTime,
a.put_amount putAmount,a.state,a.create_time createTime,a.remark
from tb_acct_activity a,tb_province b
where a.province_id=b.province_id
and a.be_deleted=0

    and a.activity_name like CONCAT('%',TRIM(#{activityName}),'%')


    and a.province_id=#{provinceId}


     and a.start_time  >= #{startStartTime}


     and a.start_time  <= #{endStartTime}

ORDER BY ${sort} ${order}
LIMIT #{offset}, #{limit}

select count(1) from ( select a.activity_id activityId,a.activity_name activityName,a.activity_type activityType,
a.channel_id channelId,b.province_name provinceName,a.start_time startTime,a.end_time endTime,
a.put_amount putAmount
from tb_acct_activity a,tb_province b
where a.province_id=b.province_id
and a.be_deleted =0

    and a.activity_name like CONCAT('%',TRIM(#{activityName}),'%')


    and a.province_id=#{provinceId}


     and a.start_time  >= #{startStartTime}


     and a.start_time  <= #{endStartTime}

) t1

select count(1) from ( select a.activity_id activityId,a.activity_name activityName,a.activity_type activityType, a.channel_id channelId,b.province_name provinceName,a.start_time startTime,a.end_time endTime, a.put_amount putAmount from tb_acct_activity a,tb_province b where a.province_id=b.province_id and a.be_deleted =0
and a.activity_name =#{activityName}
) t1
