mapper.select:
SELECT a.user_manager_log_id userManagerLogId, a.user_manager_id userManagerId, a.user_name userName, a.operation_type operationType, a.user_ip userIp, a.operation_time operationTime, b.caption, c.remark, c.new_value newValue, c.old_value oldValue FROM tb_user_manager_log a,tb_menu b,tb_user_manager_log_remark c WHERE a.user_manager_log_id=c.user_manager_log_id AND a.menu_id=b.menu_id AND a.operation_time between #{startDate} AND #{endDate}
and a.user_name like CONCAT('%',TRIM(#{userName}),'%')
and a.menu_id like CONCAT('%',TRIM(#{menuId}),'%')
and a.operation_type like CONCAT('%',TRIM(#{operationType}),'%')
ORDER BY ${sort} ${order} LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM ( SELECT a.user_manager_log_id userManagerLogId, a.user_manager_id userManagerId, a.user_name userName, a.operation_type operationType, a.user_ip userIp, a.operation_time operationTime, b.caption, c.remark FROM tb_user_manager_log a,tb_menu b,tb_user_manager_log_remark c WHERE a.user_manager_log_id=c.user_manager_log_id AND a.menu_id=b.menu_id AND a.operation_time between #{startDate} AND #{endDate}
and a.user_name like CONCAT('%',TRIM(#{userName}),'%')
and a.menu_id like CONCAT('%',TRIM(#{menuId}),'%')
and a.operation_type like CONCAT('%',TRIM(#{operationType}),'%')
) t
