mapper.select:
select offer_id offerId,offer_name offerName, initial_weight initialWeight,additional_weight additionalWeight, send_area_id sendAreaId,disp_area_id dispAreaId,is_enabled isEnabled, sort_no sortNo,create_time createTime, (select GROUP_CONCAT( province_name) from tb_offer_area_province t1,tb_province t2 where t1.province_id=t2.province_id AND t1.offer_area_id =send_area_id group by t1.offer_area_id ) sendProvinceName , (select GROUP_CONCAT( province_name) from tb_offer_area_province t1,tb_province t2 where t1.province_id=t2.province_id AND t1.offer_area_id =disp_area_id group by t1.offer_area_id ) dispProvinceName , (select full_name from (select GROUP_CONCAT( full_name) full_name,t1.offer_id from tb_offer_express_company t1,tb_express_company t2 where t1.express_company_id=t2.express_company_id group by t1.offer_id)tt where tt.offer_id= tb_offer.offer_id) companyName , (select full_name from (select GROUP_CONCAT( t1.express_company_id) full_name,t1.offer_id from tb_offer_express_company t1,tb_express_company t2 where t1.express_company_id=t2.express_company_id group by t1.offer_id)tt where tt.offer_id= tb_offer.offer_id) companyIds , (select full_name from (select GROUP_CONCAT( t1.college_id) full_name,t1.offer_id from tb_offer_college t1,tb_college t2 where t1.college_id=t2.college_id group by t1.offer_id)tt where tt.offer_id= tb_offer.offer_id) collageIds , (select offer_area_name from tb_offer_area where offer_area_id =send_area_id )sendArearName, (select offer_area_name from tb_offer_area where offer_area_id =disp_area_id )dispAreaName FROM tb_offer where is_deleted=0
and offer_name like CONCAT('%',TRIM(#{offerName}),'%')
and send_area_id=#{sendAreaId}
and disp_area_id=#{dispAreaId}
ORDER BY ${sort} ${order} LIMIT #{offset}, #{limit}

select count(1) from (
select offer_id offerId,offer_name offerName,
initial_weight initialWeight,additional_weight additionalWeight,
send_area_id sendAreaId,disp_area_id dispAreaId,is_enabled isEnabled,
sort_no sortNo,create_time createTime FROM tb_offer
where is_deleted=0

    and offer_name like CONCAT('%',TRIM(#{offerName}),'%')


    and send_area_id=#{sendAreaId}


    and disp_area_id=#{dispAreaId}

) t1

select count(1) from ( select offer_id offerId,offer_name offerName, initial_weight initialWeight,additional_weight additionalWeight, send_area_id sendAreaId,disp_area_id dispAreaId,is_enabled isEnabled, sort_no sortNo,create_time createTime FROM tb_offer where is_deleted=0
and offer_name =#{offerName}
) t1
