mapper.select:
select

from tb_dept
where be_deleted=0 and dept_id = #{deptId,jdbcType=BIGINT}

mapper.delete:
update tb_dept set
be_deleted=1
where be_deleted=0 and dept_id = #{deptId,jdbcType=BIGINT}

mapper.select:
select count(1) from tb_dept t where (t.dept_name = #{deptName,jdbcType=VARCHAR} or t.dept_code = #{deptCode,jdbcType=BIGINT})
AND t.dept_id != #{deptId,jdbcType=BIGINT}
and t.be_deleted=0
