mapper.select:
  	select

  	from tb_menu where 1=1 

  		and caption like CONCAT('%',#{caption},'%')

  	ORDER BY
		${sort} ${order}
		LIMIT #{offset}, #{limit}

select count(1) from tb_menu


		caption = #{caption,jdbcType=VARCHAR}


		and parent_id = #{parentId,jdbcType=BIGINT}


		and menu_id != #{menuId,jdbcType=BIGINT}
