mapper.select:
SELECT sms_model_id smsModelId,
user_id userId,
template_name templateName,
model_content modelContent FROM tb_sms_model_for_user
WHERE 1 = 1

    and template_name like CONCAT('%',TRIM(#{templateName}),'%')


    and user_id=#{userId}

ORDER BY ${sort} ${order}
LIMIT #{offset}, #{limit}

SELECT DATE_FORMAT( submit_time, "%Y-%m-%d" ) submitTime,
COUNT( * ) smsCount
FROM tb_sms
WHERE 1 = 1

     and submit_time  >= #{startDate}


     and submit_time  <= #{endDate}


    and user_id=#{userId}

GROUP BY DATE_FORMAT( submit_time, "%Y-%m-%d" )
ORDER BY ${sort} ${order}
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM (
SELECT DATE_FORMAT( submit_time, "%Y-%m-%d" ) submitTime,
COUNT( * ) smsCount
FROM tb_sms
WHERE 1 = 1

     and submit_time  >= #{startDate}


     and submit_time  <= #{endDate}


    and user_id=#{userId}

GROUP BY DATE_FORMAT( submit_time, "%Y-%m-%d" )) t
