mapper.select:
select t.acct_id acctId,CASE t.active_state WHEN 0 THEN '未激活'WHEN 1
THEN '激活' END activeStateName,t.state,CASE t.state WHEN 0 THEN '已锁定'
WHEN 1 THEN '已解锁' END stateName,t2.nickname nickName,
t.balance,t.frozen_balance frozenBalance,date_format(t.update_time,'%Y-%m-%d %h:%i:%s') activeTime,t2.user_name
userName,t.active_state activeState,t.user_id userId
from tb_acct_user t LEFT JOIN tb_user t2 ON t2.user_id=t.user_id

    t2.be_deleted=0

        and t2.user_name like '${userName}%'


        and   t.update_time>#{activeStartTime}


        and t.update_time<#{activeEndTime}


        and t.active_state=#{activeState}


        and t.state=#{state}

select a.acct_id acctId,d.user_name userName, b.type_name,e.acct_no acctNo,a.after_balance afterBalance,a.create_time createTime, a.opt_amount optAmount, CASE a.state WHEN 1 THEN '有效' WHEN 0 THEN '无效' ELSE '无效' END state, CASE f.pay_type WHEN 1 THEN '充值' WHEN 2 THEN '提现' WHEN 3 THEN '收款' ELSE '其他' END paymentName, a.flow_id flowId,a.before_balance beforeBalance,f.pay_type payType from tb_acct_user_detail a LEFT JOIN ts_acct_thd_payment_type b ON a.thd_type=b.type_code LEFT JOIN tb_acct_user c ON c.acct_id=a.acct_id LEFT JOIN tb_user d on d.user_id=c.user_id LEFT JOIN tb_acct_thirdparty e ON a.acct_id=e.acct_id LEFT JOIN ts_acct_payment f ON f.payment_code=a.payment_code
1=1
and a.acct_id =#{acctId}
and a.create_time>#{startTime}
and a.create_time<#{endTime}
and f.pay_type=#{payType}
